mapper.select:
SELECT to_char(A .fmc_id) "fmcId", A .fmc_nm "fmcNm" FROM td_dds_fmc A
where A.region_id =#{ query.regionId }
ORDER BY A .region_nm, A .fmc_nm

SELECT DISTINCT to_char(t.asc_grp_id) "ascGrpId", t.asc_grp_nm "ascGrpNm" FROM td_dds_asc t
where t.region_id =#{ query.regionId }
ORDER BY t.asc_grp_id, t.asc_grp_nm

SELECT to_char(a.city_id) "cityId", a.city_cn_nm "cityCnNm" FROM td_dds_city a
where a.prov_id =#{ query.provId }
ORDER BY a.city_id


    SELECT
        to_char(G .asc_id) "ascId",
        G .asc_cd || '_' || G .asc_nm "ascNm"
    FROM
        TD_DDS_ASC G
    WHERE
        1 = 1
    AND G .asc_st <> 3


    AND G .region_id =#{ query.regionId }


    AND G .FMC_ID =#{ query.fmcId }


    AND G .asc_grp_id =#{ query.ascGrpId }


    AND G .prov_id =#{ query.provId }


    AND G .city_id =#{ query.cityId }


    AND G .ASC_ID =#{ query.ascCd }

ORDER BY
G .asc_cd

SELECT period_cd "periodCd", #{query.orgTp} "orgTp", org_id "orgId", COUNT (kpi_val) "kpiVal", COUNT (lp_kpi_val) "lpKpiVal", COUNT (ly_kpi_val) "lyKpiVal"
FROM TF_KPI_RPT
AND ORG_ID = #{query.orgId}
AND ORG_ID IN
#{item}
AND period_tp = #{query.periodTp}
AND period_cd = #{query.periodCd}
AND org_tp = #{query.orgTp}
GROUP BY PERIOD_CD, ORG_ID ORDER BY ORG_ID ASC
